"use strict";

window.ui.logger('sausage loaded');

window.ui.logger();
window.ui.logger('Requiring dependency "beans" from "sausage"', '#ffa');

try
{
	require('beans');
}
catch (e)
{
	window.ui.logger(e.message, 'red');
	window.ui.logger('"beans" was expected to fail in order to outline the the handling of nested node_modules directories. Notice that the "Prefix ..." messages skip the node_modules sub-directories and stop at /app which contained the top-most node_modules folder in the search path. This is in keeping with the Node.js resolution algorithm.');
}
